// Dictionary for colorizing text printed to console.log()
exports.text_coloration = function () {

 // Example String makes 'love' appear red
 // \033[0;31mlove\033[0m

 var ans = {
  'black':'0;30',
  'dark_gray':'\033[1;30m',
  'red':'\033[0;31m',
  'light_red':'\033[1;31m',
  'green':'\033[0;32m',
  'light_green':'\033[1;32m',
  'brown_orange':'\033[0;33m',
  'yellow':'\033[1;33m',
  'blue':'\033[0;34m',
  'light_blue':'\033[1;34m',
  'purple':'\033[0;35m',
  'light_purple':'\033[1;35m',
  'cyan':'\033[0;36m',
  'light_cyan':'\033[1;36m',
  'light_gray':'\033[0;37m',
  'white':'\033[1;37m',
  'end':'\033[0m'
 }

 return ans;

};
