// Dictionary for colorizing text printed to console.log()
exports.date_diff = function (start_date=new Date, end_date=new Date, return_format='raw') {
 var date_diff = end_date.getTime() - start_date.getTime();
 var Seconds_from_T1_to_T2 = date_diff / 1000;
 var Seconds_Between_Dates = Math.abs(Seconds_from_T1_to_T2);
 console.log(return_format);
 return Seconds_Between_Dates;
}

// Dictionary for colorizing text printed to console.log()
exports.text_coloration = function () {

 // Example String makes 'love' appear red
 // \033[0;31mlove\033[0m

 // ans means answer
 // default return variable used by isajediknight
 var ans = {
  // Actual Colors
  'black'        :'\033[0;30m',
  'dark_gray'    :'\033[1;30m',
  'red'          :'\033[0;31m',
  'light_red'    :'\033[1;31m',
  'green'        :'\033[0;32m',
  'light_green'  :'\033[1;32m',
  'brown_orange' :'\033[0;33m',
  'yellow'       :'\033[1;33m',
  'blue'         :'\033[0;34m',
  'light_blue'   :'\033[1;34m',
  'purple'       :'\033[0;35m',
  'light_purple' :'\033[1;35m',
  'cyan'         :'\033[0;36m',
  'light_cyan'   :'\033[1;36m',
  'light_gray'   :'\033[0;37m',
  'white'        :'\033[1;37m',

  // Shortcuts
  'hidden'       :'\033[0;30m', //black
  'hidden!'      :'\033[1;30m', //dark_gray
  'error'        :'\033[0;31m', //red		-- Errors
  'error!'       :'\033[1;31m', //light_red	-- Fatal Errors
  'passed'       :'\033[0;32m', //green		-- Correct or Valid
  'passed!'      :'\033[1;32m', //light_green
  'notify'       :'\033[0;33m', //brown_orange	-- Notifications or Advice
  'warning!'     :'\033[1;33m', //yellow
  'metric'       :'\033[0;34m', //blue
  'metric!'      :'\033[1;34m', //light_blue
  'user'         :'\033[0;35m', //purple	-- Use for user inputed data
  'user!'        :'\033[1;35m', //light_purple	-- Use to tell the user to do something
  'date'         :'\033[0;36m', //cyan
  'date!'        :'\033[1;36m', //light_cyan
  'subtle'       :'\033[0;37m', //light_gray
  'white!'       :'\033[1;37m', //white

  // Closing Tag
  'end':'\033[0m'
 }

 return ans;

};
// End exports.text_coloration


